main = do
  (!n, !m) <- ints2
  !es <- U.replicateM m ints110

  let gr = buildWSG (0, n - 1) $ swapDupeW es

  let res = pushBasedConstructIV (<>) vec0 f
        where
          bnd = zero2 (bit n) n
          vec0 = generateIV bnd $ \(!set, !v) -> if set == bit v then Max 0 else mempty
          f sofar (!set, !v1)
            | acc == mempty = U.empty
            | otherwise = U.map g . note (set, v1, acc) $ U.filter (not . testBit set . fst) $ gr `adjW` v1
            where
              acc = sofar @! (set, v1)
              g (!v2, !dw) = ((set + bit v2, v2), acc + Max dw)

  print $ getMax $ U.maximum (vecIV res)
